 }
 export default function Emjoi(){
   const [emojiList, setEmojilist] = useState([
    {emoji: 'ðŸ˜‚', votes:0},
    {emoji:'ðŸ¤–', votes:0},
    {emoji: 'ðŸ˜Ž', votes:0},
   ]);
   function handleIncrement(){
        setEmojilist(emojiList.votes+1)
   }

   return(
    <div>
    <h2> Emoji Voter Board</h2>
    {emojiList.map((emojiList, index) => (
        <>
        <EmojiDisplay
             key={index}
          emoji={emojiList.emoji}
          vote={emojiList.votes}
        />
        <Increment onClick={handleIncrement}/>
        </>
    ))}
    </div>
   );
 }
